import React, { Component } from 'react';
import Navigation from './components/navigation/Navigation';
import Logo from './components/logo/Logo';
import ImageLinkForm from './components/imageLinkForm/ImageLinkForm';
import Rank from './components/rank/Rank';
import FaceRecognition from './components/faceRecognition/FaceRecognition';
import Particles from "react-tsparticles"; 
import Clarifai from 'clarifai';
import './App.css';



const app = new Clarifai.App({
  apiKey: '9598e591fcaa4ccaad9449177fb57125',
});

const particlesOptions = {
  fpsLimit: 120,
  interactivity: {
    events: {
      onClick: {
        enable: true,
        mode: "push",
      },
      resize: true,
    },
    modes: {
      bubble: {
        distance: 400,
        duration: 2,
        opacity: 0.8,
        size: 40,
      },
      push: {
        quantity: 4,
      },
      repulse: {
        distance: 200,
        duration: 0.4,
      },
    },
  },
  particles: {
    color: {
      value: "#ffffff",
    },
    links: {
      color: "#ffffff",
      distance: 150,
      enable: true,
      opacity: 0.5,
      width: 1,
    },
    collisions: {
      enable: true,
    },
    move: {
      direction: "none",
      enable: true,
      outMode: "bounce",
      random: false,
      speed: 1,
      straight: false,
    },
    number: {
      density: {
        enable: true,
        area: 800,
      },
      value: 100,
    },
    opacity: {
      value: 0.5,
    },
    shape: {
      type: "circle",
    },
    size: {
      random: true,
      value: 5,
    },
  },
  detectRetina: true,
}


class App extends Component{

  constructor(){
    super();
    this.state = {
      input:'',
      imageUrl: '',
    }
  }

  onInputChange = (event) =>{
    console.log(event.target.value);
    this.setState({input: event.target.value});
  }

  onSummit = () =>{
    console.log('click');
    this.setState({imageUrl:this.state.input});

    // app.model.predict(Clarifai.COLOR_MODEL, "https://images.unsplash.com/photo-1597223557154-721c1cecc4b0?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=880&q=80").then(
    //   function(response){
    //     console.log(response)
    //   },
    //   function(err){

    //   }
    // );  
  }

  render() {    
    return (
      <div className="App">
        <Particles className='particles'
          id="tsparticles"
          options={particlesOptions}
        />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm
         onInputChange = {this.onInputChange} 
         onSummit = {this.onSummit}
        />        
        <FaceRecognition imageUrl = {this.state.imageUrl}/>
      </div>
    );
  }
}

export default App;
